firestore.rules:
rules_version = '2';

service cloud.firestore {
  match /databases/{database}/documents {

    function isAuthOwner(userId) {
      return request.auth != null && request.auth.uid == userId;
    }

    function isAuthAdmin() {
      return request.auth != null && get(/databases/$(database)/documents/users/$(request.auth.uid)).data.role == 'admin';
    }

    match /users/{userId} {
      allow read: if isAuthOwner(userId) || isAuthAdmin();
      allow write: if isAuthAdmin();
    }

    match /students/{studentId} {
      allow read: if request.auth != null;
      allow write: if isAuthAdmin();
    }

    match /supervisors/{supervisorId} {
      allow read: if request.auth != null;
      allow write: if isAuthAdmin();
    }

    match /graduationDefenseSessions/{sessionId} {
      allow read, list: if request.auth != null;
      allow write: if isAuthAdmin();
    }

    match /defenseRegistrations/{registrationId} {
      allow read, list: if request.auth != null;
      allow write: if isAuthAdmin();
    }

    match /{document=**} {
      allow read, write: if false;
    }
  }
}
